package com.hpwenxue.config;import com.hpwenxue.domain.User;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * 初始化配置：用于配置系统初始化数据 * @author : liuwenlong * @date : 2018/1/15 14:50 */@Configurationpublic class InitConfig {    @Bean    public User initUser(){        User user = new User();        user.setUserName("admin");        user.setRealName("管理员");        user.setAge(25);        user.setPhotograph("null");        return user;    }}