package com.hpwenxue.controller;import com.hpwenxue.domain.ArticleType;import com.hpwenxue.service.ArticleTypeService;import com.hpwenxue.utils.R;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;/** * 文章类型管理 * @author : liuwenlong * @date : 2017/12/24 11:49 */@RestController@RequestMapping("/articles-type")public class ArticlesTypeController {    @Autowired    private ArticleTypeService articleTypeService;    /**     * 查询类型清单     * @return     */    @GetMapping("/list")    public R list(){        return R.ok().put("date",articleTypeService.findAll());    }    /**     * 保存     * @param articleType     * @return     */    @PostMapping(value = "/save")    public R save(@RequestBody ArticleType articleType){        articleTypeService.save(articleType);        return R.ok();    }    public R delete(@RequestBody List<Integer> ids){        articleTypeService.delete(ids);        return R.ok();    }}