package com.hpwenxue.domain;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;/** * 保留字：在注册、发文、等时候，部分关键字不能使用，获取 使用 **** 代替 * @author : liuwenlong * @date : 2018/1/15 14:56 */@Entitypublic class ReservedWords {    @GeneratedValue    @Id    Integer id;    String detail;    /**     * 1标识用 *** 代替，2 表示直接提示不能使用     */    Integer type;    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public String getDetail() {        return detail;    }    public void setDetail(String detail) {        this.detail = detail;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    @Override    public String toString() {        return "ReservedWords{" +                "id=" + id +                ", detail='" + detail + '\'' +                ", type=" + type +                '}';    }}