package com.hpwenxue.service.impl;import com.hpwenxue.domain.ArticleType;import com.hpwenxue.repository.ArticleTypeRepository;import com.hpwenxue.service.ArticleTypeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author : liuwenlong * @date : 2017/12/29 10:15 */@Servicepublic class ArticleTypeServiceImpl implements ArticleTypeService{    @Autowired    private ArticleTypeRepository articleTypeRepository;    @Override    public List<ArticleType> findAll() {        return articleTypeRepository.findAll();    }    @Override    public void save(ArticleType articleType) {        articleTypeRepository.save(articleType);    }    @Override    public void delete(ArticleType articleType) {        articleTypeRepository.delete(articleType);    }    @Override    public void delete(Integer[] ids) {        for (Integer id : ids ){            articleTypeRepository.delete(id);        }    }}