package com.hpwenxue.service.impl;import com.hpwenxue.dao.UserRepository;import com.hpwenxue.domain.User;import com.hpwenxue.service.LoginService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;/** * @author : liuwenlong * @date : 2017/12/22 22:22 */@Servicepublic class LoginServiceImpl implements LoginService{    private Logger logger = LoggerFactory.getLogger(getClass());    @Autowired    private UserRepository userRepository;    @Override    public Map<String, Object> login(String userName, String password) {        User user = userRepository.findByUserName(userName);        logger.debug("登陆成功，用户名："+userName +",密码："+password);        return null;    }    @Override    public void logout(User user) {    }}