package com.hpwenxue.utils;import java.util.ArrayList;/** * page 分页工具 * @author : gitor * @date : 2017/11/29 10:57 */public class P {    private Integer resentPage;    private Integer totalPage;    private Integer total;    private ArrayList<?> list;    public Integer getResentPage() {        return resentPage;    }    public void setResentPage(Integer resentPage) {        this.resentPage = resentPage;    }    public Integer getTotalPage() {        return totalPage;    }    public void setTotalPage(Integer totalPage) {        this.totalPage = totalPage;    }    public Integer getTotal() {        return total;    }    public void setTotal(Integer total) {        this.total = total;    }    public ArrayList<?> getList() {        return list;    }    public void setList(ArrayList<?> list) {        this.list = list;    }    @Override    public String toString() {        return "P{" +                "resentPage=" + resentPage +                ", totalPage=" + totalPage +                ", total=" + total +                ", list=" + list +                '}';    }}